import { useState } from "react";
import Square from "./Square";

const Board = () => {
  function calcWinner(squares) {
    const lines = [
      [0, 1, 2],
      [3, 4, 5],
      [6, 7, 8], //ROWS
      [0, 3, 6],
      [1, 4, 7],
      [2, 5, 8], //COLUMNS
      [0, 4, 8],
      [2, 4, 6], //DIAGONAL
    ];
    for (let line of lines) {
      const [a, b, c] = line;

      if (square[a] && square[a] === square[b] && square[a] === square[c]) {
        return square[a];
      }
      return null;
    }
  }

  const winner = calcWinner(square)

  const initialSquares = Array(9).fill(null);

  const [square, setSquare] = useState(initialSquares);
  const [xIsNext, setXIsNext] = useState(true);

  const clickEventHandler = (i) => {
    const newSquares = [...square];
    newSquares[i] = winner ? `${winner} wins!` : xIsNext ? "X" : "O";
    setSquare(newSquares);
    setXIsNext(!xIsNext);
  };

  const status = ` "${xIsNext ? "X" : "O"}" plays next`;

  const renderSquare = (i) => {
    return (
      <div>
        <Square value={square[i]} onClickEvent={() => clickEventHandler(i)} />
      </div>
    );
  };

  return (
    <div className="board">
      <div className="status">{status}</div>
      <div className="square-box">
        {renderSquare(0)} {renderSquare(1)} {renderSquare(2)}
      </div>
      <div className="square-box">
        {renderSquare(3)} {renderSquare(4)} {renderSquare(5)}
      </div>
      <div className="square-box">
        {renderSquare(6)} {renderSquare(7)} {renderSquare(8)}
      </div>
    </div>
  );
};
export default Board;
